if (globalVars("giveawayStartAlreadyRan") == null) {

    this.storeValue(true, 3, "giveawayStartAlreadyRan", cache);

    const fs = require('fs');
    const request = require('request');
    const version = "1.3";

    request('https://raw.githubusercontent.com/ItzPike/DBM-Rawdata-Releases/master/Giveaway%20Rewritten/version.txt', function (error, response, body) {
            if(response.statusCode == 200) {
                if(version != body) {
                    console.log("[Giveaway Rewritten] New giveaway rewritten version avaliable!");
                }
            }
        });

    if (!fs.existsSync('./data/giveawayData.json')) {
        fs.writeFile("./data/giveawayData.json", "{}", function(err) {
            if (err) {
                return console.log(err);
            }
        });
    }
}
