    if (globalVars("inviteManagerRanAlready") == null) {

        Actions.storeValue(true, 3, "inviteManagerRanAlready", cache);

        const fs = require('fs');
        const request = require('request');
        const Files = Actions.getDBM().Files;

        //request('https://raw.githubusercontent.com/ItzPike/DBM-Rawdata-Releases/master/Invite%20Manager/versionCheck.json', function (error, response, body) {
            //if(response.statusCode == 200) {
                var allVersions = {"events":[["EqQxg","1"]], "commands":[["ppGiU","1"],["TWxen","1"],["CcKpI","1"],["MaaAe","1"]]};

                var commandVersions = {};
                var eventVersions = {};
                var commandKeys = [];
                var eventKeys = [];

                allVersions["commands"].forEach(commandVer => {
                    commandVersions[commandVer[0]] = commandVer[1];
                    commandKeys.push(commandVer[0]);
                });

                allVersions["events"].forEach(eventVer => {
                    eventVersions[eventVer[0]] = eventVer[1];
                    eventKeys.push(eventVer[0])
                })

                fs.readFile("./data/commands.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemCommands = await data.filter(command => command != null && commandKeys.includes(command["_id"]));

                    for (i=0; i<systemCommands.length; i++) {
                        var systemActionFind = await systemCommands[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemActionFind.length != 0) {
                            var versionCheckSplit = systemActionFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit[1] != null && versionCheckSplit[1] != commandVersions[systemCommands[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Invite Manager] The command ${systemCommands[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })

                fs.readFile("./data/events.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemEvent = await data.filter(event => event != null && eventKeys.includes(event["_id"]));

                    for (i=0; i<systemEvent.length; i++) {
                        var systemEventFind = await systemEvent[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemEventFind.length != 0) {
                            var versionCheckSplit = systemEventFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit != null && versionCheckSplit[versionCheckSplit.length - 1] != eventVersions[systemEvent[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Invite Manager] The event ${systemEvent[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })
            //}
