const reaction = tempVars('msgReaction');
const msgRct = tempVars('messageRct');
var rectarray = globalVars('reactionRoleInf');
let usrWhoReacted = reaction.users.array();
const _this = this;

var messageSent = 'Successfully removed the role {roleName} in {server}';
// The only variables that work rn, {rolename} and {server}
var actualUser;
// Setings

const dmUserAfter = true; // Default, can be changed to false

//
async function checkRoles() {
    try {
        if (rectarray[msgRct.id] != null) {
            var userarrayplacement;
            if (!isNaN(rectarray[msgRct.id][rectarray[msgRct.id].length - 1][1])) {
                userarrayplacement = rectarray[msgRct.id].length
            } else {
                userarrayplacement = rectarray[msgRct.id].length - 1
            }
            if (rectarray[msgRct.id][userarrayplacement] != null) {
                usrWhoReacted = rectarray[msgRct.id][userarrayplacement].filter(val => !usrWhoReacted.includes(val))[0];

                if (usrWhoReacted != null) {
                    if (usrWhoReacted.data('cooldownData.reactionMenu') != 1) {
                        var newArr = rectarray[msgRct.id];
                        for (io = 0; io < newArr.length; io++) {
                            if (newArr[io][0] == reaction.emoji.name || newArr[io][0].name == reaction.emoji.name) {
                                var newUsr = await msgRct.guild.fetchMember(usrWhoReacted);
                                var checkRole = await msgRct.guild.roles.get(newArr[io][1]);
                                if (checkRole != null) {
                                    if (!newUsr.roles.has(newArr[io][1])) {
                                        usrWhoReacted.setData('cooldownData.reactionMenu', '1');
                                        var tempThing = globalVars('reactionRoleInf');
                                        tempThing[msgRct.id][userarrayplacement] = await reaction.users.array();
                                        _this.storeValue(tempThing, 3, 'reactionRoleInf', cache);
                                        interval = setInterval(resetTimer, 1000);
                                        break;
                                    } else {
                                        var tempThing = globalVars('reactionRoleInf');
                                        usrWhoReacted.setData('cooldownData.reactionMenu', '1');
                                        tempThing[msgRct.id][userarrayplacement] = await reaction.users.array();
                                        _this.storeValue(tempThing, 3, 'reactionRoleInf', cache)
                                        newUsr.removeRole(checkRole);
                                        if (dmUserAfter) {
                                            messageSent = messageSent.replace(/{roleName}/g, checkRole.name);
                                            messageSent = messageSent.replace(/{server}/g, msgRct.guild.name);
                                            newUsr.send(messageSent);
                                        }
                                        interval = setInterval(resetTimer, 500);
                                        break;
                                    }
                                } else {
                                    delete rectarray[msgRct.id];
                                    _this.storeValue(rectarray, 3, 'reactionRoleInf', cache);
                                    msgRct.edit({
                                        embed: {
                                            color: 0xff0000,
                                            title: "Role Reaction Menu",
                                            description: `An error has occured.\nOne of the roles, role name - ${newArr[io][2]} does not exist anymore.\nIn turn, this embed has now shutdown.\nPlease remake the embed.`,
                                            timestamp: new Date()
                                        }
                                    });
                                    _this.callNextAction(cache);
                                    break;
                                }
                            }
                        }
                    } else {
                        usrWhoReacted.setData('cooldownData.reactionMenu', '0');
                    }
                }
            }
        }
    } catch (error) {
        console.log(error);
    }

}

function resetTimer() {
    usrWhoReacted.setData('cooldownData.reactionMenu', '0');
    clearInterval(interval);
}

checkRoles();
